window._winManager.alert = (title, json) =>{
    return {
        id: 'win-alert',
        title: title,
        pos:{
            left: 'calc(50% - 100px)',
            top: '40%',
        },
        size:{
            maxwidth: '300px'
        },
        content: [ json ],
        onready: win =>{
            const EL = win.el
            $DOM.once(EL, ()=>{
                $(EL).addClass('win-alert').parent().json2html({}, DOM.newEl('div',{ class: 'alert-background'}))
            })
        },
        onremove: win =>{
            console.log("remove")
            $('.alert-background').remove()
        }
    }
}
