window._winManager = {}
runtimeScene.getLayer("Grid").setFollowBaseLayerCamera(true)
runtimeScene.getLayer("Layer0").setFollowBaseLayerCamera(true)
runtimeScene.getLayer("Layer1").setFollowBaseLayerCamera(true)
runtimeScene.getLayer("Layer2").setFollowBaseLayerCamera(true)

const DOM   = gdjs._json2html
const $DOM  = gdjs._DOMapi
const $WIN  = gdjs._windowManager
const $TILE = gdjs._TileSetMapEditor

setTimeout(()=>{
    let win = gdjs._windowManager.newWindow(window._winManager.devOS)
}, 500)
    
window._createWin = {
    title:'create new window',
    id:'new_window',
    content:[
        {
            "<>":"input",
            "class":"input",
            "placeholder": "Title",
            "ref": "win_title"
        },
        {   "<>":"br" },
        {
            "<>":"textarea",
            "class":"input",
            "placeholder": "Body",
            "ref": "win_body"
        },
        {   "<>":"br" },
        
        {   "<>":"button",
            "class":"button is-primary",
            "ref": "new-window",
            "html":"create",
            "onclick": function(){
                let titleInput = gdjs._DOMapi.selectElbyRef("win_title")
                let bodyInput = gdjs._DOMapi.selectElbyRef("win_body")
                gdjs._windowManager.newWindow({
                    title: titleInput.val(),
                    content: bodyInput.val()
                })
            }
        }
    ]
}
